import  {fromJS} from 'immutable';

const defaultState = fromJS({
  login: false,
  id: null
});

export default (state = defaultState, action) =>{

  if (action.type === 'log'){
    console.log(1);
    return state.set('login', true);
  }
  if (action.type ==='change_log_state'){
    console.log(action);
    return state;
  }
  return state;
}
