import firebase from "firebase";

// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyA-6f33cQXGlP5iFYn-z1Wt0FMD4kI-Kwc",
  authDomain: "facebook-clone-c732d.firebaseapp.com",
  databaseURL: "https://facebook-clone-c732d.firebaseio.com",
  projectId: "facebook-clone-c732d",
  storageBucket: "facebook-clone-c732d.appspot.com",
  messagingSenderId: "882076079069",
  appId: "1:882076079069:web:a3c45954acf9820d36dc34",
  measurementId: "G-XL9LH9WZLV",
};

const firebaseApp = firebase.initialize(firebaseConfig);
const db = firebaseApp.firestore();
const auth = firebase.auth();
//this will tell firebase that we need google login service
const provider = new firebase.auth.GoogleAuthProvider();

export { auth, provider};
export default db;
