import React, {Component} from "react";
import "./Feed.css";
import StoryReel from "./StoryReel";
import MessageSender from "./MessageSender";
import Post from "./Post";
import  * as actionCreators from './store/actionCreators';
import {connect} from "react-redux";
import axios from 'axios';

class Feed extends Component {
  render() {
    return (
        <div className="feed">
          <StoryReel/>
          <MessageSender/>
          {
            this.props.articleList.map((item
            ) => {
              return (
                  <Post
                      profilePic="/logo.png"
                      title={item.get('title')}
                      desc={item.get('desc')}
                      timestamp={item.get('timestamp')}
                      username={item.get('username')}
                      imgUrl={item.get('imgUrl')}
                  />
              )
            })
          }
          <div className="loadMore" onClick={this.props.getMoreList}>Load More</div>
        </div>
    );
  }

  componentDidMount() {
    this.props.getFeedData();
  }
}

const mapDispatchToProps = (dispatch) =>{
  return{
    getFeedData(){
      axios.get('/api/feed.json').then((res) => {
        const result = res.data.data;
        const action = {
          type: 'get_feed_data',
          articleList: result.articleList
        }
        dispatch(action);
      })
    },

    getMoreList(){
      dispatch(actionCreators.getMoreList());
    }
  }
}

const mapStateToProps = (state) =>{
  return{
    // 第一个get('login')指的是login中的store中的reducer
    articleList: state.get('feed').get('articleList')
  }
}

export default connect(mapStateToProps,mapDispatchToProps)(Feed);
