import  {fromJS} from 'immutable';

const defaultState = fromJS({
  articleList: []
});

export default (state = defaultState, action) =>{
  switch (action.type) {
    case 'get_feed_data':
      console.log(action.articleList);
      return state.set('articleList', fromJS(action.articleList));
    case 'add_feed_list':
      console.log(action.list.articleList);
      return state.set('articleList', state.get('articleList').concat(action.articleList));
      return state.set('articleList', fromJS(action.list));
    default:
      return state;
  }
}
